import { useCallback, useMemo, useRef } from 'react';
import { Button, StyleSheet, Text, View } from 'react-native';
import { FlatList, TouchableOpacity } from 'react-native-gesture-handler';
import { Entypo } from '@expo/vector-icons';
import BottomSheet, { BottomSheetFooter } from '@gorhom/bottom-sheet';
import { BottomSheetDefaultFooterProps } from '@gorhom/bottom-sheet/lib/typescript/components/bottomSheetFooter/types';

import useImagePicker from '@/hooks/useImagePicker';
import { FontSizeColorStyle, LayoutStyle } from '@/styles';
import Animated from 'react-native-reanimated';

const EditPhotos = () => {
	const { images, openCamera, openLibrary } = useImagePicker({ multiple: true });

	const {
		images: images2,
		openCamera: openCamera2,
		openLibrary: openLibrary2,
	} = useImagePicker({ multiple: true });

	const bottomSheetRef = useRef<BottomSheet>(null);

	const bottomSheetRef2 = useRef<BottomSheet>(null);

	// variables
	const snapPoints = useMemo(() => ['1%', '25%'], []);

	// renders
	const renderFooter = useCallback(
		(props: BottomSheetDefaultFooterProps) => (
			<BottomSheetFooter {...props} bottomInset={24}>
				<View style={styles.contentContainer}>
					<Text>Open Camera or Choose From Photos</Text>
				</View>
			</BottomSheetFooter>
		),
		[]
	);

	return (
		<View style={[LayoutStyle.screenLayout, LayoutStyle.fillWhite]}>
			<View style={{ width: '100%', justifyContent: 'space-between', flexDirection: 'row' }}>
				<Text style={FontSizeColorStyle['opensans-600-18-dark-4a4a4a']}>Public Photos</Text>
				<Text style={FontSizeColorStyle['opensans-600-12-dark-4a4a4a']}>(0/2)</Text>
			</View>

			<FlatList
				contentContainerStyle={{ gap: 5 }}
				numColumns={4}
				data={images || new Array(8).fill(undefined)}
				columnWrapperStyle={{ gap: 5 }}
				renderItem={({ item }) => (
					<TouchableOpacity onPress={() => bottomSheetRef?.current?.expand()}>
						<Animated.Image
							style={{ height: 100, width: 100 }}
							source={
								item || {
									uri: 'data:image/jpeg;base64,',
								}
							}
						/>
					</TouchableOpacity>
				)}
			/>
			<View style={{ width: '100%', justifyContent: 'space-between', flexDirection: 'row' }}>
				<View>
					<Text style={FontSizeColorStyle['opensans-600-18-dark-4a4a4a']}>
						Private Photos
					</Text>
					<Text style={FontSizeColorStyle['opensans-600-12-dark-4a4a4a']}>
						Private Photos can only be Viewed after request{' '}
					</Text>
				</View>
				<Text style={FontSizeColorStyle['opensans-600-12-dark-4a4a4a']}>(0/2)</Text>
			</View>

			<FlatList
				contentContainerStyle={{ gap: 5 }}
				numColumns={4}
				data={images2 || new Array(8).fill(undefined)}
				columnWrapperStyle={{ gap: 5 }}
				renderItem={({ item }) => (
					<TouchableOpacity onPress={() => bottomSheetRef2?.current?.expand()}>
						<Animated.Image
							style={{ height: 100, width: 100 }}
							source={
								item || {
									uri: 'data:image/jpeg;base64,',
								}
							}
						/>
					</TouchableOpacity>
				)}
			/>

			<Button color={'#F44586'} title="Save" />
			<BottomSheet
				ref={bottomSheetRef2}
				index={1}
				snapPoints={snapPoints}
				footerComponent={renderFooter}
				onClose={() => bottomSheetRef2?.current?.close()}
			>
				<View style={styles.footerContainer}>
					<TouchableOpacity onPress={openCamera2}>
						<Entypo name="camera" size={24} color="black" />
					</TouchableOpacity>
					<TouchableOpacity onPress={openLibrary2}>
						<Entypo name="images" size={24} color="black" />
					</TouchableOpacity>
				</View>
			</BottomSheet>
			<BottomSheet
				ref={bottomSheetRef}
				index={1}
				snapPoints={snapPoints}
				footerComponent={renderFooter}
				onClose={() => bottomSheetRef?.current?.close()}
			>
				<View style={styles.footerContainer}>
					<TouchableOpacity onPress={openCamera}>
						<Entypo name="camera" size={24} color="black" />
					</TouchableOpacity>
					<TouchableOpacity onPress={openLibrary}>
						<Entypo name="images" size={24} color="black" />
					</TouchableOpacity>
				</View>
			</BottomSheet>
		</View>
	);
};

export default EditPhotos;

const styles = StyleSheet.create({
	container: {
		flex: 1,
		padding: 24,
		backgroundColor: 'grey',
	},
	contentContainer: {
		flex: 1,
		alignItems: 'center',
	},
	footerContainer: {
		padding: 12,
		margin: 12,
		borderRadius: 12,
		backgroundColor: '#e1e1e1',
		flexDirection: 'row',
		gap: 10,
	},
	footerText: {
		textAlign: 'center',
		color: 'white',
		fontWeight: '800',
	},
} as const);
